package strings;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

class FindTheDifference389Test {

    @Test
    void firstTest() {
        assertEquals('e', new FindTheDifference389().findTheDifference("abcd", "abcde"));
    }

    @Test
    void secondTest() {
        assertEquals('y', new FindTheDifference389().findTheDifference("", "y"));
    }

    @Test
    void longTest() {
        var s = "";
        var t = "";

        assertEquals('t', new FindTheDifference389().findTheDifference(s, t));
    }
}